package op;

public class Divop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 7;
		int num2 = 3;
		
		//정수의 나누기 연산 
		System.out.println("나누기 연산="+num1/num2);
		
		//실수의 나누기 연산 
		System.out.println("실수 나누기 연산="+ 7f/3f);
		System.out.println("다른방법으로 실수나누기="+ (float)num1/num2); 
		
		//
}

}
